import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

function ProfilePage({ userId }) {
  const [birthYear, setBirthYear] = useState("");
  const [password, setPassword] = useState("");
  const [avatar, setAvatar] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [message, setMessage] = useState("");

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await axios.get(`http://localhost:3001/users/${userId}`);
        if (res.data.avatar) {
          setPreviewUrl(`http://localhost:3001${res.data.avatar}`);
        }
        setBirthYear(res.data.birth_year || "");
      } catch (err) {
        console.error("Kļūda ielādējot profilu:", err);
      }
    };
    fetchProfile();
  }, [userId]);

  const handleAvatarChange = (e) => {
    const file = e.target.files[0];
    setAvatar(file);
    setPreviewUrl(URL.createObjectURL(file));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    if (password) formData.append("password", password);
    if (birthYear) formData.append("birth_year", birthYear);
    if (avatar) formData.append("avatar", avatar);

    try {
      await axios.put(`http://localhost:3001/users/${userId}`, formData, {
        headers: { "Content-Type": "multipart/form-data" }
      });
      setMessage("✅ Profils atjaunināts veiksmīgi!");
    } catch (err) {
      console.error("Kļūda saglabājot profilu:", err);
      setMessage("❌ Kļūda saglabājot datus");
    }
  };

  return (
    <div className="container mt-4">
      <Link to="/dashboard" className="btn btn-secondary mb-3">
        🔙 Atpakaļ uz paneli
      </Link>
      <h2>👤 Profils</h2>
      {message && <p>{message}</p>}

      <div className="mb-3 text-center">
        {previewUrl ? (
          <img src={previewUrl} alt="Avatar" width="120" className="rounded-circle" />
        ) : (
          <span style={{ fontSize: "80px" }}>👤</span>
        )}
      </div>

      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <div className="mb-3">
          <label className="form-label">Jauna parole:</label>
          <input
            type="password"
            className="form-control"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Ievadi jauno paroli"
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Dzimšanas gads:</label>
          <input
            type="text"
            className="form-control"
            value={birthYear}
            onChange={(e) => setBirthYear(e.target.value)}
            placeholder="Ievadi dzimšanas gadu"
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Augšupielādēt avataru:</label>
          <input
            type="file"
            className="form-control"
            accept="image/*"
            onChange={handleAvatarChange}
          />
        </div>

        <button type="submit" className="btn btn-primary">
          💾 Saglabāt izmaiņas
        </button>
      </form>
    </div>
  );
}

export default ProfilePage;
