import React, { useState, useEffect } from "react";
import axios from "axios";

function ProfilePage({ userId }) {
  const [password, setPassword] = useState("");
  const [birthYear, setBirthYear] = useState("");
  const [avatar, setAvatar] = useState(null);
  const [preview, setPreview] = useState(null);
  const [currentAvatar, setCurrentAvatar] = useState(null);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await axios.get(`http://localhost:3001/profile/${userId}`);
        setBirthYear(res.data.birth_year || "");
        // Ja avataru neglabā DB, tad šeit var nebūt avatara URL
        // Tā kā avataru neglabā, mēs to rādīsim tikai no upload sesijas
      } catch (err) {
        console.error("Kļūda ielādējot profilu:", err);
      }
    };
    fetchProfile();
  }, [userId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    if (password) formData.append("password", password);
    if (birthYear) formData.append("birth_year", birthYear);
    if (avatar) formData.append("avatar", avatar);

    try {
      const res = await axios.put(`http://localhost:3001/profile/${userId}`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert("✅ Profils saglabāts!");
      if (res.data.avatarUrl) {
        setCurrentAvatar(`http://localhost:3001${res.data.avatarUrl}`);
      }
      setPreview(null);
      setPassword("");
      setAvatar(null);
    } catch (err) {
      console.error("Kļūda saglabājot profilu:", err);
      alert("❌ Kļūda saglabājot profilu");
    }
  };

  return (
    <div className="container mt-4">
      <h2>👤 Profils</h2>

      <div className="mb-3">
        <img
          src={
            preview
              ? URL.createObjectURL(preview)
              : currentAvatar || "https://cdn-icons-png.flaticon.com/512/847/847969.png"
          }
          alt="Avatar"
          style={{ width: "120px", height: "120px", borderRadius: "50%", objectFit: "cover" }}
        />
      </div>

      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <div className="mb-3">
          <label>Dzimšanas gads:</label>
          <input
            type="number"
            className="form-control"
            value={birthYear}
            onChange={(e) => setBirthYear(e.target.value)}
            placeholder="Ievadi dzimšanas gadu"
          />
        </div>

        <div className="mb-3">
          <label>Jauna parole:</label>
          <input
            type="password"
            className="form-control"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Ievadi jauno paroli"
          />
        </div>

        <div className="mb-3">
          <label>Augšupielādēt avataru:</label>
          <input
            type="file"
            className="form-control"
            accept="image/*"
            onChange={(e) => {
              setAvatar(e.target.files[0]);
              setPreview(e.target.files[0]);
            }}
          />
        </div>

        <button type="submit" className="btn btn-primary">
          💾 Saglabāt
        </button>
      </form>
    </div>
  );
}

export default ProfilePage;
