import React, { useState } from "react";
import axios from "axios";

function AddStudent() {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [password, setPassword] = useState("");
  const [avatar, setAvatar] = useState("");

  const handleAdd = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post("http://localhost:3001/students/add", {
        first_name: firstName,
        last_name: lastName,
        password,
        avatar,
      });

      alert("✅ Skolēns pievienots!");
      setFirstName("");
      setLastName("");
      setPassword("");
      setAvatar("");
    } catch (err) {
      console.error(err);
      alert("❌ Kļūda pievienojot skolēnu");
    }
  };

  return (
    <form onSubmit={handleAdd} className="p-4 border rounded space-y-2">
      <input
        type="text"
        placeholder="Vārds"
        value={firstName}
        onChange={(e) => setFirstName(e.target.value)}
        required
        className="border p-2 w-full"
      />
      <input
        type="text"
        placeholder="Uzvārds"
        value={lastName}
        onChange={(e) => setLastName(e.target.value)}
        required
        className="border p-2 w-full"
      />
      <input
        type="password"
        placeholder="Parole"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required
        className="border p-2 w-full"
      />
      <input
        type="text"
        placeholder="Profila bildes URL (neobligāts)"
        value={avatar}
        onChange={(e) => setAvatar(e.target.value)}
        className="border p-2 w-full"
      />
      <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">
        ➕ Pievienot skolēnu
      </button>
    </form>
  );
}

export default AddStudent;
