import React, { useState, useEffect } from "react";
import axios from "axios";

function ProfilePage() {
  const [password, setPassword] = useState("");
  const [birthYear, setBirthYear] = useState("");
  const [currentAvatar, setCurrentAvatar] = useState(null);

  useEffect(() => {
    axios.get("http://localhost:3001/profile").then(res => {
      setBirthYear(res.data.birth_year || "");
      if (res.data.avatar) {
        setCurrentAvatar(`http://localhost:3001${res.data.avatar}`);
      }
    });
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put("http://localhost:3001/profile", { birth_year: birthYear, password });
      alert("‚úÖ Profils saglabƒÅts!");
      setPassword("");
    } catch (err) {
      alert("‚ùå Kƒº≈´da saglabƒÅjot profilu");
    }
  };

  return (
    <div className="container mt-4">
      <h2>üë§ Profils</h2>

      <div className="mb-3">
        <img
          src={
            currentAvatar ||
            "https://cdn-icons-png.flaticon.com/512/847/847969.png"
          }
          alt="Avatar"
          style={{ width: "120px", height: "120px", borderRadius: "50%", objectFit: "cover" }}
        />
      </div>

      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label>Gadskaitlis:</label>
          <input
            type="number"
            className="form-control"
            value={birthYear}
            onChange={(e) => setBirthYear(e.target.value)}
          />
        </div>

        <div className="mb-3">
          <label>Jauna parole:</label>
          <input
            type="password"
            className="form-control"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Ievadi jauno paroli"
          />
        </div>

        <button type="submit" className="btn btn-primary">
          üíæ SaglabƒÅt
        </button>
      </form>
    </div>
  );
}

export default ProfilePage;
