import React, { useState, useEffect } from "react";
import axios from "axios";

function ProfilePage({ userId }) {
  const [birthYear, setBirthYear] = useState("");
  const [password, setPassword] = useState("");
  const [avatarFile, setAvatarFile] = useState(null);
  const [userData, setUserData] = useState(null);
  const [message, setMessage] = useState("");

  useEffect(() => {
    async function fetchProfile() {
      try {
        const res = await axios.get(`http://localhost:3001/profile/${userId}`);
        setUserData(res.data);
        setBirthYear(res.data.birth_year || "");
      } catch (err) {
        console.error("Kļūda ielādējot profilu:", err);
      }
    }
    fetchProfile();
  }, [userId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    if (password) formData.append("password", password);
    if (birthYear) formData.append("birth_year", birthYear);
    if (avatarFile) formData.append("avatar", avatarFile);

    try {
      await axios.put(`http://localhost:3001/profile/${userId}`, formData, {
        headers: { "Content-Type": "multipart/form-data" }
      });
      setMessage("✅ Profils atjaunināts veiksmīgi!");
      // Atsvaidzina datus
      const res = await axios.get(`http://localhost:3001/profile/${userId}`);
      setUserData(res.data);
      setPassword("");
      setAvatarFile(null);
    } catch (err) {
      console.error("Kļūda saglabājot profilu:", err);
      setMessage("❌ Kļūda saglabājot datus");
    }
  };

  if (!userData) return <p>Ielādē...</p>;

  return (
    <div className="container mt-4">
      <h2>👤 Profils</h2>
      {message && <p>{message}</p>}
      <div>
        <img
          src={`http://localhost:3001${userData.avatar || "/default-avatar.png"}`}
          alt="Avatar"
          style={{ width: 120, height: 120, borderRadius: "50%", objectFit: "cover" }}
        />
      </div>

      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <div className="mb-3">
          <label>Dzimšanas gads:</label>
          <input
            type="number"
            className="form-control"
            value={birthYear}
            onChange={(e) => setBirthYear(e.target.value)}
          />
        </div>

        <div className="mb-3">
          <label>Jauna parole:</label>
          <input
            type="password"
            className="form-control"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Ievadi jauno paroli"
          />
        </div>

        <div className="mb-3">
          <label>Augšupielādēt avataru:</label>
          <input
            type="file"
            accept="image/*"
            className="form-control"
            onChange={(e) => setAvatarFile(e.target.files[0])}
          />
        </div>

        <button type="submit" className="btn btn-primary">
          💾 Saglabāt izmaiņas
        </button>
      </form>
    </div>
  );
}

export default ProfilePage;
