import React, { useState } from "react";
import axios from "axios";

function AddGradeForm({ studentId, onClose, onGradeAdded }) {
  const [subject, setSubject] = useState("latviesu valoda");
  const [grade, setGrade] = useState(1);
  const subjects = [
    "latviesu valoda",
    "anglu valoda",
    "matematika",
    "sports",
    "programmesana",
    "eikt",
    "uznemej darbiba",
    "bialogija",
    "fizika"
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:3001/grades/add", {
        student_id: studentId,
        subject,
        grade
      });
      onGradeAdded();
      onClose();
    } catch (err) {
      console.error("Kļūda pievienojot atzīmi:", err);
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <h3>Pievienot atzīmi</h3>
        <form onSubmit={handleSubmit}>
          <label>Priekšmets:</label>
          <select value={subject} onChange={(e) => setSubject(e.target.value)}>
            {subjects.map((s, idx) => (
              <option key={idx} value={s}>{s}</option>
            ))}
          </select>

          <label>Atzīme:</label>
          <input
            type="number"
            min="1"
            max="10"
            value={grade}
            onChange={(e) => setGrade(e.target.value)}
            required
          />

          <div className="form-buttons">
            <button type="submit">Saglabāt</button>
            <button type="button" onClick={onClose}>Atcelt</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default AddGradeForm;
