import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

function ProfilePage({ userId }) {
  const [birthYear, setBirthYear] = useState("");
  const [password, setPassword] = useState("");
  const [avatar, setAvatar] = useState(null);
  const [avatarPreview, setAvatarPreview] = useState(null); // ✅
  const [message, setMessage] = useState("");

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await axios.get(`http://localhost:3001/users/${userId}`);
        setBirthYear(res.data.birth_year || "");
        if (res.data.avatar) {
          setAvatarPreview(`http://localhost:3001${res.data.avatar}`);
        }
      } catch (err) {
        console.error("Kļūda ielādējot profilu:", err);
      }
    };
    fetchProfile();
  }, [userId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    if (password) formData.append("password", password);
    if (avatar) formData.append("avatar", avatar);
    if (birthYear) formData.append("birth_year", birthYear);

    try {
      await axios.put(`http://localhost:3001/users/${userId}`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setMessage("✅ Profils atjaunināts veiksmīgi!");

      // Parāda jauno avatar priekšskatījumu, ja fails tika mainīts
      if (avatar) {
        setAvatarPreview(URL.createObjectURL(avatar));
      }
    } catch (err) {
      console.error("Kļūda saglabājot profilu:", err);
      setMessage("❌ Kļūda saglabājot datus");
    }
  };

  const handleAvatarChange = (e) => {
    const file = e.target.files[0];
    setAvatar(file);
    setAvatarPreview(URL.createObjectURL(file)); // priekšskatījums
  };

  return (
    <div className="container mt-4">
      <Link to="/dashboard" className="btn btn-secondary mb-3">
        🔙 Atpakaļ uz paneli
      </Link>
      <h2>👤 Profils</h2>
      {message && <p>{message}</p>}

      {/* Avatar priekšskatījums */}
      <div className="mb-3 text-center">
        {avatarPreview ? (
          <img
            src={avatarPreview}
            alt="Avatar"
            style={{ width: "120px", height: "120px", borderRadius: "50%", objectFit: "cover" }}
          />
        ) : (
          <div style={{ fontSize: "4rem" }}>👤</div>
        )}
      </div>

      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <div className="mb-3">
          <label className="form-label">Jauna parole:</label>
          <input
            type="password"
            className="form-control"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Ievadi jauno paroli"
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Dzimšanas gads:</label>
          <input
            type="number"
            className="form-control"
            value={birthYear}
            onChange={(e) => setBirthYear(e.target.value)}
            placeholder="Piemēram: 2005"
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Augšupielādēt avataru:</label>
          <input
            type="file"
            className="form-control"
            accept="image/*"
            onChange={handleAvatarChange}
          />
        </div>

        <button type="submit" className="btn btn-primary">
          💾 Saglabāt izmaiņas
        </button>
      </form>
    </div>
  );
}

export default ProfilePage;
