import React, { useState, useEffect } from "react";
import axios from "axios";

function ProfilePage({ userId }) {
  const [birthYear, setBirthYear] = useState("");
  const [password, setPassword] = useState("");
  const [avatar, setAvatar] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:3001/profile/${userId}`).then((res) => {
      setBirthYear(res.data.birth_year || "");
      setPreviewUrl(res.data.avatar_url || null);
    });
  }, [userId]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("birth_year", birthYear);
    if (password) formData.append("password", password);
    if (avatar) formData.append("avatar", avatar);

    try {
      await axios.put(`http://localhost:3001/profile/${userId}`, formData);
      alert("Profils atjauninƒÅts!");
    } catch (err) {
      alert("Kƒº≈´da atjauninot profilu");
      console.error(err);
    }
  };

  return (
    <div>
      <h2>Profils</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Dzim≈°anas gads:</label>
          <input
            type="number"
            value={birthYear}
            onChange={(e) => setBirthYear(e.target.value)}
          />
        </div>
        <div>
          <label>Jauna parole:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Neaiztiec, ja nevƒìlies mainƒ´t"
          />
        </div>
        <div>
          <label>Profila bilde:</label>
          <input
            type="file"
            accept="image/*"
            onChange={(e) => {
              setAvatar(e.target.files[0]);
              setPreviewUrl(URL.createObjectURL(e.target.files[0]));
            }}
          />
          {previewUrl && <img src={previewUrl} alt="Avatar" height="100" />}
        </div>
        <button type="submit">üíæ SaglabƒÅt</button>
      </form>
    </div>
  );
}

export default ProfilePage;
