import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Dashboard.css";

function Dashboard() {
  const [students, setStudents] = useState([]);
  const [userRole, setUserRole] = useState("");

  useEffect(() => {
    // 🔒 Nolasām lietotāja lomu no localStorage
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (storedUser) {
      setUserRole(storedUser.role);
    }

    // 📥 Ielādējam skolēnus
    axios.get("http://localhost:3001/students")
      .then((res) => setStudents(res.data))
      .catch((err) => console.error("Kļūda ielādējot skolēnus:", err));
  }, []);

  return (
    <div className="dashboard">
      <h2 className="dashboard-title">🎓 Skolēnu panelis</h2>

      <div className="dashboard-table-wrapper">
        <table className="dashboard-table">
          <thead>
            <tr>
              <th>Vārds</th>
              <th>Uzvārds</th>
              <th>Mācību priekšmeti</th>
              <th>Atlikušās dienas</th>
              <th>Vidējā atzīme</th>
              {userRole !== "student" && <th>Darbības</th>}
            </tr>
          </thead>
          <tbody>
            {students.map((s) => (
              <tr key={s.id}>
                <td>{s.first_name}</td>
                <td>{s.last_name}</td>
                <td>Matemātika, Latviešu val., Ģeogrāfija</td>
                <td>{calculateDaysLeft(s)} dienas</td>
                <td className="average">Nav atzīmju</td>
                {userRole !== "student" && (
                  <td>
                    <button className="add-grade-btn">➕ Pievienot atzīmi</button>
                  </td>
                )}
              </tr>
            ))}
            {students.length === 0 && (
              <tr>
                <td colSpan={userRole !== "student" ? 6 : 5} className="no-students">
                  Nav neviena skolēna.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

function calculateDaysLeft(student) {
  const endDate = new Date("2025-06-30");
  const today = new Date();
  const diff = endDate - today;
  return Math.ceil(diff / (1000 * 60 * 60 * 24));
}

export default Dashboard;
