import React, { useState } from "react";
import axios from "axios";

const AddStudent = ({ onStudentAdded }) => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [password, setPassword] = useState("");
  const [avatarUrl, setAvatarUrl] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post("http://localhost:3001/students/add", {
        first_name: firstName,
        last_name: lastName,
        password: password,
        avatarUrl: avatarUrl
      });

      alert(res.data.message);
      if (onStudentAdded) onStudentAdded(); // Refresh student list
      setFirstName("");
      setLastName("");
      setPassword("");
      setAvatarUrl("");
    } catch (error) {
      console.error("❌ Kļūda pievienojot skolēnu:", error);
      alert("Neizdevās pievienot skolēnu");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-md mx-auto p-4 bg-white shadow-md rounded">
      <h2 className="text-xl font-semibold mb-4">Pievienot skolēnu</h2>

      <div className="mb-3">
        <label className="form-label">Vārds</label>
        <input
          type="text"
          className="form-control"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
          required
        />
      </div>

      <div className="mb-3">
        <label className="form-label">Uzvārds</label>
        <input
          type="text"
          className="form-control"
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
          required
        />
      </div>

      <div className="mb-3">
        <label className="form-label">Parole</label>
        <input
          type="text"
          className="form-control"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
      </div>

      <div className="mb-3">
        <label className="form-label">Profila bilde (URL)</label>
        <input
          type="text"
          className="form-control"
          value={avatarUrl}
          onChange={(e) => setAvatarUrl(e.target.value)}
        />
      </div>

      <button type="submit" className="btn btn-primary w-full">
        Pievienot
      </button>
    </form>
  );
};

export default AddStudent;
