import React, { useEffect, useState } from "react";
import axios from "axios";

function ProfilePage({ userId }) {
  const [data, setData] = useState({ username: "", birth_year: "", avatar: "" });
  const [password, setPassword] = useState("");
  const [avatar, setAvatar] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:3001/profile/${userId}`)
      .then(res => setData(res.data))
      .catch(err => console.error("Kƒº≈´da ielƒÅdƒìjot profilu", err));
  }, [userId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("birth_year", data.birth_year);
    formData.append("password", password);
    if (avatar) formData.append("avatar", avatar);

    try {
      await axios.put(`http://localhost:3001/profile/${userId}`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert("Profils atjaunots!");
    } catch (err) {
      alert("Kƒº≈´da saglabƒÅjot datus");
    }
  };

  return (
    <div style={{ maxWidth: "400px", margin: "auto" }}>
      <h2>üßë‚Äçüíª Profils</h2>
      <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: 10 }}>
        <label>VƒÅrds: {data.username}</label>

        <label>Dzim≈°anas gads:</label>
        <input type="number" value={data.birth_year || ""} onChange={(e) => setData({ ...data, birth_year: e.target.value })} />

        <label>Parole:</label>
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />

        <label>Avatar:</label>
        <input type="file" accept="image/*" onChange={(e) => setAvatar(e.target.files[0])} />

        {data.avatar && <img src={`http://localhost:3001${data.avatar}`} alt="Avatar" width={100} />}

        <button type="submit">üíæ SaglabƒÅt</button>
      </form>
    </div>
  );
}

export default ProfilePage;
