import React, { useEffect, useState } from "react";
import axios from "axios";
import AddGradeForm from "./AddGradeForm";

function Dashboard() {
  const [students, setStudents] = useState([]);
  const [search, setSearch] = useState("");
  const [showFormFor, setShowFormFor] = useState(null);
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Ielādē pašreizējo lietotāju no localStorage
    const userData = JSON.parse(localStorage.getItem("user"));
    setUser(userData);

    // Ielādē skolniekus no servera
    axios.get("http://localhost:3001/students")
      .then((res) => setStudents(res.data))
      .catch((err) => console.error("Kļūda ielādējot skolēnus:", err));
  }, []);

  // Meklēšanas filtrs
  const filtered = students.filter(s =>
    s.first_name.toLowerCase().includes(search.toLowerCase()) ||
    s.last_name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div style={{ padding: 24, backgroundColor: "#f3f4f6", minHeight: "100vh" }}>
      <h2 style={{ fontSize: 28, fontWeight: "bold", marginBottom: 20, textAlign: "center" }}>
        🎓 Skolēnu panelis
      </h2>

      <input
        style={{
          marginBottom: 16,
          padding: 8,
          borderRadius: 6,
          border: "1px solid #ccc",
          width: "100%",
          maxWidth: 400,
          display: "block",
          marginLeft: "auto",
          marginRight: "auto",
          fontSize: 16,
        }}
        placeholder="Meklēt skolēnu pēc vārda vai uzvārda"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />

      <table
        style={{
          width: "100%",
          maxWidth: 800,
          marginLeft: "auto",
          marginRight: "auto",
          borderCollapse: "collapse",
          backgroundColor: "#fff",
          boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
        }}
      >
        <thead style={{ backgroundColor: "#2563eb", color: "#fff" }}>
          <tr>
            <th style={{ padding: 12 }}>Profila bilde</th>
            <th style={{ padding: 12 }}>Vārds</th>
            <th style={{ padding: 12 }}>Uzvārds</th>
            <th style={{ padding: 12 }}>Priekšmeti</th>
            <th style={{ padding: 12 }}>Atlikušās dienas</th>
            <th style={{ padding: 12 }}>Vidējā atzīme</th>
            <th style={{ padding: 12 }}>Darbības</th>
          </tr>
        </thead>
        <tbody>
          {filtered.length === 0 ? (
            <tr>
              <td colSpan="7" style={{ padding: 20, textAlign: "center", color: "#777" }}>
                Nav atrasts neviens skolēns.
              </td>
            </tr>
          ) : (
            filtered.map((s) => {
              const isCurrentUser = user && s.id === user.id;

              return (
                <tr
                  key={s.id}
                  style={{
                    backgroundColor: isCurrentUser ? "#bfdbfe" : "transparent",
                    transition: "background-color 0.3s",
                  }}
                >
                  <td style={{ padding: 12, textAlign: "center" }}>
                    <img
                      src={s.avatar_url || "/default-avatar.png"}
                      alt="Profila bilde"
                      style={{ width: 40, height: 40, borderRadius: "50%", objectFit: "cover" }}
                    />
                  </td>
                  <td style={{ padding: 12, fontWeight: "600" }}>{s.first_name}</td>
                  <td style={{ padding: 12 }}>{s.last_name}</td>
                  <td style={{ padding: 12, color: "#444" }}>
                    Matemātika, Latviešu val., Ģeogrāfija
                  </td>
                  <td style={{ padding: 12 }}>
                    {calculateDaysLeft(s)} dienas
                  </td>
                  <td style={{ padding: 12, color: "#fbbf24", fontWeight: "700" }}>
                    {calculateAverage(s)}
                  </td>
                  <td style={{ padding: 12 }}>
                    {user?.role === "teacher" && (
                      <button
                        style={{
                          backgroundColor: "#3b82f6",
                          color: "#fff",
                          padding: "6px 12px",
                          borderRadius: 6,
                          border: "none",
                          cursor: "pointer",
                          fontWeight: "600",
                        }}
                        onClick={() => setShowFormFor(s.id)}
                      >
                        ➕ Pievienot atzīmi
                      </button>
                    )}
                    {showFormFor === s.id && (
                      <AddGradeForm studentId={s.id} onClose={() => setShowFormFor(null)} />
                    )}
                  </td>
                </tr>
              );
            })
          )}
        </tbody>
      </table>
    </div>
  );
}

function calculateDaysLeft(student) {
  const endDate = new Date("2025-06-30");
  const today = new Date();
  const diff = endDate - today;
  return diff > 0 ? Math.ceil(diff / (1000 * 60 * 60 * 24)) : 0;
}

function calculateAverage(student) {
  // šeit var likt īstu aprēķinu no atzīmēm
  return "Nav atzīmju";
}

export default Dashboard;
