import React, { useState, useEffect } from "react";
import axios from "axios";

function ProfilePage({ userId }) {
  const [password, setPassword] = useState("");
  const [birthYear, setBirthYear] = useState("");
  const [avatarUrl, setAvatarUrl] = useState("");

  useEffect(() => {
    axios.get(`http://localhost:3001/profile/${userId}`)
      .then(res => {
        setBirthYear(res.data.birth_year || "");
        setAvatarUrl(res.data.avatar || "");
      })
      .catch(err => console.error("Kƒº≈´da ielƒÅdƒìjot profilu:", err));
  }, [userId]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.put(`http://localhost:3001/profile/${userId}`, {
        password,
        birth_year: birthYear,
        avatar_url: avatarUrl,
      });

      alert("‚úÖ Profils saglabƒÅts!");
      setPassword("");
    } catch (err) {
      console.error("‚ùå Kƒº≈´da saglabƒÅjot profilu:", err);
      alert("NeizdevƒÅs saglabƒÅt");
    }
  };

  return (
    <div className="container mt-4">
      <h2>üë§ Profils</h2>

      <div className="mb-3">
        <img
          src={avatarUrl || "https://cdn-icons-png.flaticon.com/512/847/847969.png"}
          alt="Avatar"
          style={{ width: "120px", height: "120px", borderRadius: "50%", objectFit: "cover" }}
        />
      </div>

      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label>Dzim≈°anas gads:</label>
          <input
            type="number"
            className="form-control"
            value={birthYear}
            onChange={(e) => setBirthYear(e.target.value)}
          />
        </div>

        <div className="mb-3">
          <label>Jauna parole:</label>
          <input
            type="password"
            className="form-control"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>

        <div className="mb-3">
          <label>Profila attƒìla saite (URL):</label>
          <input
            type="text"
            className="form-control"
            value={avatarUrl}
            onChange={(e) => setAvatarUrl(e.target.value)}
            placeholder="https://..."
          />
        </div>

        <button type="submit" className="btn btn-primary">üíæ SaglabƒÅt</button>
      </form>
    </div>
  );
}

export default ProfilePage;
