import React, { useState, useEffect } from "react";
import axios from "axios";

function ProfilePage({ userId }) {
  const [password, setPassword] = useState("");
  const [avatar, setAvatar] = useState(null);
  const [preview, setPreview] = useState(null);
  const [currentAvatar, setCurrentAvatar] = useState("");

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await axios.get(`http://localhost:3001/users/${userId}`);
        if (res.data.avatar) {
          setCurrentAvatar(`http://localhost:3001${res.data.avatar}`);
        }
      } catch (err) {
        console.error("Kƒº≈´da ielƒÅdƒìjot profilu:", err);
      }
    };
    fetchProfile();
  }, [userId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    if (password) formData.append("password", password);
    if (avatar) formData.append("avatar", avatar);

    try {
      await axios.put(`http://localhost:3001/users/${userId}`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert("‚úÖ Profils saglabƒÅts!");
      if (avatar) {
        setCurrentAvatar(URL.createObjectURL(avatar)); // preview uzreiz
      }
    } catch (err) {
      console.error("Kƒº≈´da saglabƒÅjot profilu:", err);
    }
  };

  return (
    <div className="container mt-4">
      <h2>üë§ Profils</h2>

      <div className="mb-3">
        <img
          src={
            preview
              ? URL.createObjectURL(preview)
              : currentAvatar || "https://cdn-icons-png.flaticon.com/512/847/847969.png"
          }
          alt="Avatar"
          style={{ width: "120px", height: "120px", borderRadius: "50%", objectFit: "cover" }}
        />
      </div>

      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <div className="mb-3">
          <label>Jauna parole:</label>
          <input
            type="password"
            className="form-control"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Ievadi jauno paroli"
          />
        </div>

        <div className="mb-3">
          <label>Aug≈°upielƒÅdƒìt avataru:</label>
          <input
            type="file"
            className="form-control"
            accept="image/*"
            onChange={(e) => {
              setAvatar(e.target.files[0]);
              setPreview(e.target.files[0]);
            }}
          />
        </div>

        <button type="submit" className="btn btn-primary">
          üíæ SaglabƒÅt
        </button>
      </form>
    </div>
  );
}

export default ProfilePage;
