import React, { useState, useEffect } from "react";
import axios from "axios";

function AddGradeForm({ studentId, onClose }) {
  const [subjects, setSubjects] = useState([]);
  const [subjectId, setSubjectId] = useState("");
  const [grade, setGrade] = useState("");
  const [teacherName, setTeacherName] = useState("");

  useEffect(() => {
    axios.get("http://localhost:3001/subjects")
      .then(res => setSubjects(res.data))
      .catch(err => console.error("Kļūda ielādējot priekšmetus:", err));
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:3001/grades/add", {
        student_id: studentId,
        subject_id: subjectId,
        grade,
        teacher_name: teacherName
      });
      onClose();
    } catch (err) {
      alert("Kļūda pievienojot atzīmi");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="form-box">
      <h3>Pievienot atzīmi</h3>
      <select value={subjectId} onChange={(e) => setSubjectId(e.target.value)} required>
        <option value="">Izvēlies priekšmetu</option>
        {subjects.map(subj => <option key={subj.id} value={subj.id}>{subj.subject_name}</option>)}
      </select>
      <input
        type="number"
        placeholder="Atzīme (1-10)"
        value={grade}
        onChange={(e) => setGrade(e.target.value)}
        required
      />
      <input
        placeholder="Skolotāja vārds"
        value={teacherName}
        onChange={(e) => setTeacherName(e.target.value)}
        required
      />
      <button type="submit">Saglabāt</button>
      <button type="button" onClick={onClose}>Aizvērt</button>
    </form>
  );
}

export default AddGradeForm;
